note_models.css:
.card {
	font-family: Menlo, Regular, sans;
	font-size: 16px;
	text-align: center;
	color: #D7DEE9;
	background-color: #272822;
}

#kard {
	padding: 0px 0px;
	max-width: 700px;
	margin: 0 auto;
	word-wrap: break-word;
	color: #D7DEE9;
	background-color: #272822;
}

.cloze {
	font-weight: bold;
	color: MediumSeaGreen !important;
}

#extra, #extra i {
	font-size: 15px;
	color: #D7DEE9.
}

.tags { 
    color: #A6ABB9;
    opacity: 0;
    font-size: 10px; 
    width: 100%;
    text-align: center;
    text-transform: uppercase; 
    position: fixed; 
    padding: 0; 
    top:0;  
    right: 0;}
.tags:hover { opacity: 1; position: fixed;}

/* IMAGE STYLE */
img { display: block; max-width: 100%; max-height: none; margin-left: auto; margin: 10px auto 10px auto;}
img:active { width: 100%; }
tr {font-size: 12px; }

/* COLOR ACCENTS FOR BOLD-ITALICS-UNDERLINE */
b { color: #F92672; !important; } /* BOLD STYLE */
u { text-decoration: none; color: #FD971F;} /* UNDERLINE STYLE */
i  { color: #66D9EF; } /* ITALICS STYLE */
.cloze i
{
	font-weight: bold;
	color: MediumSeaGreen !important;
}
.cloze b
{
	font-weight: bold;
	color: MediumSeaGreen !important;
}
.cloze u
{
	font-weight: bold;
	color: MediumSeaGreen !important;
}

/* ADJUSTMENT FOR MOBILE DEVICES */
.mobile .card { color: black; background-color: #D7DEE9; font-size: 20px} 
.modile #kard { color: #D7DEE9; }
.mobile .tags:hover { opacity: 1; position: relative;}
.mobile .card img:active { width: inherit; max-height: none;}

